import React, { useState } from 'react';
import { signInWithEmailAndPassword } from 'firebase/auth';
import { auth } from '../firebase';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

export default function SignIn() {
  const [email, setEmail] = useState('');
  const [pass, setPass] = useState('');
  const [msg, setMsg] = useState('');
  const navigate = useNavigate();

  const handleSignIn = async () => {
    try {
      // Đăng nhập Firebase
      const userCred = await signInWithEmailAndPassword(auth, email, pass);
      const token = await userCred.user.getIdToken();

      // Gửi token đến backend
      const res = await axios.post('http://localhost:8080/auth/login', { idToken: token });

      if (res.status === 200) {
        setMsg("✅ Đăng nhập thành công!");
        // Lưu UID hoặc email nếu cần
        localStorage.setItem("uid", res.data.uid);
        localStorage.setItem("email", res.data.email);
        // Chuyển hướng về trang chủ
        setTimeout(() => {
          navigate("/home");
        }, 1000); // đợi 1s để người dùng đọc message
      } else {
        setMsg("❌ Đăng nhập thất bại.");
      }

    } catch (err) {
      console.error(err);
      setMsg("❌ " + (err.response?.data || err.message));
    }
  };

  return (
    <div className="form-container">
      <h2>Đăng nhập</h2>
      <input type="email" placeholder="Email" onChange={e => setEmail(e.target.value)} />
      <input type="password" placeholder="Password" onChange={e => setPass(e.target.value)} />
      <button onClick={handleSignIn}>Đăng nhập</button>
      <p>{msg}</p>
      <p>Chưa có tài khoản? <Link to="/signup">Đăng ký</Link></p>
    </div>
  );
}
